#ifndef META_API_LINKIN_H
#define META_API_LINKIN_H

#define __META_API_QUOTE_IDENTIFIER(x) #x
#define __META_API_QUOTE_MACRO(x) __META_API_QUOTE_IDENTIFIER(x)

#define __META_API_LINKIN_PASTE(a, b) a ## b
#define __META_API_LINKIN_CONCAT_MACRO(a, b) __META_API_LINKIN_PASTE(a, b)

#define META_API_LINKIN_PREFIX __DIABLO_META_API__
#define META_API_LINKIN_FUNCTION(x) __META_API_LINKIN_CONCAT_MACRO(META_API_LINKIN_PREFIX, x)

#define META_API_LINKIN_FUNCTION_NAME(x) __META_API_QUOTE_MACRO(META_API_LINKIN_FUNCTION(x))
#define META_API_LINKIN_PREFIX_STRING __META_API_QUOTE_MACRO(META_API_LINKIN_PREFIX)

#endif
